angular.module('freshigerator', []).controller('mainController', function() {
  var vm;
  vm = this;
  vm.test = 'hello world';
  vm.itemList = [
    {
      id: 1,
      name: 'milk',
      exp: '2016-12-22'
    }, {
      id: 2,
      name: 'eggs',
      exp: '2018-12-26'
    }, {
      id: 3,
      name: 'beef',
      exp: '2017-01-01'
    }
  ];
  vm.addItem = function() {
    return vm.isNewItemForm = true;
  };
  vm.removeItem = function(item) {
    var index;
    index = vm.itemList.indexOf(item);
    return vm.itemList.splice(index, 1);
  };
  return vm.submitNewItem = function() {
    var itemToAdd;
    vm.isNewItemForm = false;
    itemToAdd = {
      id: vm.itemList.length + 1,
      name: vm.name,
      exp: vm.exp
    };
    vm.itemList.push(itemToAdd);
    vm.name = null;
    return vm.exp = null;
  };
}).directive('addItemForm', function() {
  return {
    restrict: 'E',
    templateUrl: '/add_item_form.html'
  };
});

